//     </ClientResultContact>
//   ));
// };

// renderClientResultBio = () => {
//   return this.state.iClient.map(iClient => (
//     <ClientResultBio>
//       {/* need to make a results component */}
//       {/* this needs to reference the client components */}
//       -id={iClient.id}
//       key={iClient._id}
//       ideal={iClient.q1}
//       challenge={iClient.q2}
//       environment={iClient.q3}
//       practice={iClient.q4}
//     </ClientResultBio>
//   ));
