class Client extends Component {
  render() {
    return (
      <div>
        <div className="parallax-container">
          <Container>
            <Row>
              <Col size="12">
                <div
                  className="white center"
                  id="search-heading"
                  style={{ marginTop: "5%", paddingBottom: "5px" }}
                >
                  <div className="center inline" id="search-title">
                    <img className="img" src={Headstand} />
                    <span
                      style={{
                        marginRight: "50px",
                        marginLeft: "20px",
                        fontSize: "20px",
                        fontWeight: "bold"
                      }}
                    >
                      Name:
                      <span> Jake Falco</span>
                    </span>
                    <span
                      style={{
                        marginRight: "50px",
                        marginLeft: "20px",
                        fontSize: "20px",
                        fontWeight: "bold"
                      }}
                    >
                      Level:
                      <span> Intermediate </span>
                    </span>
                    <span
                      style={{
                        marginRight: "50px",
                        marginLeft: "20px",
                        fontSize: "20px",
                        fontWeight: "bold"
                      }}
                    >
                      Location:
                      <span> San Luis Obispo, CA</span>
                    </span>
                  </div>
                </div>
              </Col>
            </Row>
            <Row>
              <Col size="6">
                <ClientCard />
              </Col>
              <Col size="6">
                <Card title="Client Biography">
                  <Row>
                    <div
                      className="col m12"
                      style={{
                        marginBottom: "30px",
                        paddingLeft: "40px",
                        paddingRight: "40px"
                      }}
                    >
                      <h6>About Me</h6>
                      <p>
                        <ClientResultContact />
                      </p>
                      {/* {this.renderClientResultContact()} */}
                      {/* <p style={{ lineHeight: "20px" }}>
                          {" "}
                          Ideal Yoga Practice:
                        </p>
                        <p style={{ lineHeight: "20px" }}>
                          {" "}
                          Challenges I am Facing in My Practice:
                        </p>
                        <p style={{ lineHeight: "20px" }}>
                          {" "}
                          Ideal Yoga Practice Environment:
                        </p> */}{" "}
                    </div>
                    <Modal
                      className=" waves-effect waves-light"
                      trigger={<Button>Fill and Display Profile</Button>}
                    >
                      <Row>
                        <h6>
                          Fill in Firstname, Lastname and Email. Click Display
                          Profile and Close to View!
                        </h6>
                      </Row>
                      <Row>
                        <Col size="6">
                          <div className="input-field col s12">
                            <i className="material-icons prefix">
                              account_circle
                            </i>
                            <input
                              id="name"
                              type="text"
                              className="validate"
                              // onChange={this.handleNameSearch}
                              // value={this.state.name}
                            />
                            <label htmlFor="name">Name</label>
                          </div>
                        </Col>
                        <Col size="6">
                          <div className="center">
                            <div className="input-field col s12">
                              <i className="material-icons prefix">
                                account_circle
                              </i>
                              <input
                                id="location"
                                type="text"
                                className="validate"
                                // onChange={this.handleLocationSearch}
                                // value={this.state.location}
                              />
                              <label htmlFor="location">Email</label>
                            </div>
                          </div>
                        </Col>
                      </Row>
                      <a
                        className="waves-effect waves-light btn-small"
                        // onClick={this.handleSearchSubmit}
                      >
                        Display Profile
                      </a>{" "}
                    </Modal>
                  </Row>
                </Card>
              </Col>
            </Row>
            <Row>
              <Col size="12">
                <div
                  className="white "
                  id="search-heading"
                  style={{
                    // marginTop: "5%",
                    paddingBottom: "5px",
                    marginLeft: "5px"
                  }}
                >
                  <div
                    className=""
                    id="search-title"
                    style={{
                      paddingLeft: "40px",
                      paddingRight: "40px"
                    }}
                  >
                    {/* <img className="img" src={OM} /> */}
                    <h4 className="center">Client Bio </h4>
                    <p>
                      <ClientResultBio />
                    </p>
                    {/* {this.renderclientProfBio()} */}
                    {/* <p
                        style={{
                          marginLeft: "50px"
                        }}
                      >
                        Here is how it all began:
                      </p> */}
                  </div>
                </div>
              </Col>
            </Row>
            <div style={{ marginBottom: "5px" }}>
              <Link to="/clientsurvey">
                {" "}
                <a className="waves-effect waves-light btn-small">
                  Take Client Survey
                </a>{" "}
              </Link>
              <Link to="/search">
                {" "}
                <a className="waves-effect waves-light btn-small">
                  Search Instructors
                </a>{" "}
              </Link>
            </div>
          </Container>
          <div className="parallax">
            <img
              className="background-img"
              src={background}
              alt="Unsplashed background img 1"
            />
          </div>

          <div className="section-white" style={{ marginTop: "5%" }}>
            <h2 className="center">Namaste</h2>
            <Row>
              <div className="center">
                <Col size="3" />
              </div>
              <div className="center">
                <Col size="6">
                  <p>
                    "Yoga Is a light which once lit will never dim, the better
                    your practice the brighter your flame."
                  </p>
                  <p>- B.K.S. Iyengar</p>
                </Col>
              </div>
              <div className="center">
                <Col size="3" />
              </div>
            </Row>
          </div>
        </div>
      </div>
    );
  }
}

export default Client;
