import React, { Component } from "react";
import { Col, Row, Container } from "../components/Grid";
import InstructorCard from "../components/InstructorCard";
import { Card, Input, Icon, Button } from "react-materialize";
import background from "../images/floor2.jpg";
// import the selected instructor data from instructorsearch.js

// arrive from profile form page
// get the instructor name and location in an object
// get the selected date and time and add the result to the modal
// need to import the instructor object or the name and do another call to the backend
// get the instructor data from the db (based on the click handler)
// ajax call for the classes data

class Booking extends Component {
  state: {
    instructorSelection: "",
    instructorData: [],
    date: "",
    time: ""
  };

  componentDidMount() {
    this.getInstructorProfile();
  }

  // api call for the instructor info (from db)

  //ajax call to backend for the instructor teaching schedule data

  handleDateInput() {
    this.setState({ date: event.target.value });
  }

  handleTimeInput() {
    this.setState({ time: event.target.value });
  }

  //method for rendering profile div

  //method for rendering the schedules div

  render() {
    return (
      <div>
        <div className="parallax-container">
          <Container>
            <div id="booking-section" className="white">
              <Row>
                <div className="white center" id="search-heading">
                  <h2 className="center" id="search-title">
                    <Icon className="medium">event</Icon>CLASS SCHEDULER
                  </h2>
                </div>
              </Row>
              <Row>
                <Col size="6">
                  <InstructorCard />
                </Col>
                <Col size="6">
                  <Card title="Request an Introductory Session">
                    <Row>
                      <div className="input-field col m12">
                        <input
                          placeholder="Date"
                          id="datePick"
                          type="text"
                          class="datepicker"
                        />
                        <label for="datePick">Select a date</label>
                      </div>
                    </Row>
                    <Row>
                      <div className="input-field col m12">
                        <input
                          placeholder="Time"
                          id="timePick"
                          type="text"
                          class="timepicker"
                        />
                        <label for="timePick">Select a time</label>
                      </div>
                    </Row>
                    <Button
                      className="waves-effect waves-light btn-small"
                      type="submit"
                    >
                      Submit
                    </Button>
                  </Card>
                </Col>
              </Row>
            </div>
          </Container>
          <div className="parallax">
            <img
              className="background-img"
              src={background}
              alt="Unsplashed background img 1"
            />
          </div>
        </div>
      </div>
    );
  }
}

export default Booking;
